"""IcareUtilities"""import os# import PathsUtilities as putil# Below, shows thta we use mostly the code provided by the Physionet2023 challengefrom utilities.Physionet2023.helper_code import find_recording_filesfrom utilities.Physionet2023.helper_code import load_challenge_datafrom utilities.Physionet2023.helper_code import get_age, get_outcome, get_cpcfrom utilities.Physionet2023.helper_code import get_utility_frequencyfrom utilities.Physionet2023.helper_code import load_recording_datadef list_patients(data_dir):    return [os.path.basename(f.path) for f in os.scandir(data_dir) if f.is_dir()]    def list_recordings(data_dir, patient_id):    recording_ids    = find_recording_files(data_dir, patient_id)    return recording_idsdef read_eeg(data_dir, recording_id):    patient_id = recording_id.split('_')[0]    group      = 'EEG'    recording_location = os.path.join(data_dir, patient_id, '{}_{}'.format(recording_id, group))    if os.path.exists(recording_location + '.hea'):        data, channels, sampling_frequency = load_recording_data(recording_location)        utility_frequency = get_utility_frequency(recording_location + '.hea')        info = {'channels': channels,            'sampling_frequency': sampling_frequency,            'utility_frequency': utility_frequency}        return data, infodef age(data_dir, patient_id):    patient_metadata = load_challenge_data(data_dir, patient_id)    return get_age(patient_metadata)def outcome(data_dir, patient_id):    patient_metadata = load_challenge_data(data_dir, patient_id)    return get_outcome(patient_metadata)def cpc(data_dir, patient_id):    patient_metadata = load_challenge_data(data_dir, patient_id)    return get_cpc(patient_metadata)    