"""TuhUtilities"""import osimport mneimport numpy as npfrom glob import globdef list_recordings(data_dir, patient_id):    recording_ids = glob(os.path.join(data_dir, '*', patient_id,'**', '*.edf'), recursive=True)    return [ os.path.relpath(recording_id, data_dir) for recording_id in recording_ids]def list_patients(data_dir):    edf_paths = list_all_edf(data_dir)    patients = [edf_path.split(os.sep)[1] for edf_path in edf_paths]    return sorted(set(patients))def list_all_edf(data_dir):    edf_paths = glob(os.path.join(data_dir, '**', '*.edf'), recursive=True)    return [ os.path.relpath(edf_path, data_dir) for edf_path in edf_paths]     def read_eeg(data_dir, recording_id):    edf  = mne.io.read_raw_edf(os.path.join(data_dir, recording_id), preload=True, verbose=0)    data = edf.get_data()    info = {'channels': edf.info['ch_names'],            'sampling_frequency': edf.info['sfreq'],            'utility_frequency': np.nan}  # not provided    return data, info